import { useState } from 'react'
import "./Login.css"

function Login (props) {
    const [display, setDisplay] = useState(false);

    const toggleDisplay = (e) => {
        e.preventDefault()
        setDisplay(!display)
    }

    if ((props.id == "Login" || props.id == "Signup") && display != true) {
        setDisplay(true);
    }
    else {
        setDisplay(false);
    }

    alert(display);

    if (display == true) {
        return (
            <div className="form-container">
                <div className="login-form">
                    <button className="u-pull-right" onClick={toggleDisplay}>Close</button>
                    <div className="u-cf"/>
                    <form className="container">
                        <h2>{props.id}</h2> 
                        <div>
                            <label for="username" className="u-full-width">Username</label>
                            <input type="email" id="username" className="u-full-width"></input>
                            <label for="password" className="u-full-width">Password</label>
                            <input type="password" id="password" className="u-full-width"></input>
                        </div>
                        <br />
                        <button type="submit" className="button-primary">{props.id}</button>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login;
